import React from "react";

function text1 () {
  console.log('texto 1');
};

function text2 () {
  console.log('texto 2');
};

function text3 () {
  console.log('texto 3');
};

class App extends React.Component{
  render(){
    return (
      <div className='App'>
        <button onClick={text1}>BTN 1</button>
        <button onClick={text2}>BTN 2</button>
        <button onClick={text3}>BTN 3</button>
      </div>
    )  
  }
}

export default App;
