import React from "react";



class App extends React.Component{
  constructor(){
    super()
    this.text1 = this.text1.bind(this)
    this.text2 = this.text2.bind(this)
    this.text3 = this.text3.bind(this)
    this.state = {
    clicks1: 0,
    clicks2: 0,
    clicks3: 0,
    }  
  }

  text1 () {
   this.setState((prevState) => ({
    click1: prevState.click1 + 1
   }))
  }

  text2 () {
    this.setState((prevState) => ({
     click2: prevState.click2 + 1
    }))
   }

   text3 () {
    this.setState((prevState) => ({
     click3: prevState.click3 + 1
    }))
   };


  render() {
    return (
      <div className='App'>
        <div>
          <button onClick={this.text1}>{this.state.click1}</button>
        </div>
        <div>
          <button onClick={this.text2}>{this.state.click2}</button>
        </div>
        <div>
          <button onClick={this.text3}>{this.state.click3}</button>
        </div>
      </div>
    )  
  }
}

export default App;
