import React from "react";



class App extends React.Component{

  state = {
    clicks1: 0,
    clicks2: 0,
    clicks3: 0,
  };

  text1 = () => {
   this.setState((prevState) => ({
    click1: prevState.clicks1 + 1
   }))
  };

  text2 = () => {
    this.setState((prevState) => ({
     click2: prevState.clicks2 + 1
    }))
   };

   text3 = () => {
    this.setState((prevState) => ({
     click3: prevState.clicks3 + 1
    }))
   };


  render() {
    return (
      <div className='App'>
        <div>
          <button onClick={this.text1}>{this.state.clicks1}</button>
        </div>
        <div>
          <button onClick={this.text2}>{this.state.clicks2}</button>
        </div>
        <div>
          <button onClick={this.text3}>{this.state.clickss3}</button>
        </div>
      </div>
    )  
  }
}

export default App;
