import React from "react";


class App extends React.Component{

  text1 = () => {
    console.log('texto 1');
  };

  text2 = () => {
    console.log('texto 2');
  };

  text3 = () => {
    console.log('texto 3');
  };

  render() {
    return (
      <div className='.App'>
        <div>
          <button onClick={this.text1}>BTN 1</button>
        </div>
        <div>
          <button onClick={this.text2}>BTN 2</button>
        </div>
        <div>
          <button onClick={this.text3}>BTN 3</button>
        </div>
      </div>
    )  
  }
}

export default App;
