import React from "react";

class App extends React.Component {
  state = {
    persons: [],
  }

  componentDidMount(){
    this.fetchPersons();
  }

  fetchPersons = async () => {
    const url =  'https://api.randomuser.me/';
    const request = await fetch(url);
    console.log(request)
  }

  render() {
    const { persons } = this.state;
    console.log(persons)
    return(
      <div>
        <h1>PERSONS</h1>
      </div>
    );
  }
}

export default App;
