import React from "react";
import Loading from "./Loading";

class PersonDetails extends React.Component {
  state = {
    persons: [],
    loading: true,
  };

  componentDidMount(){
    this.fetchPerson();
  }

  fetchPerson = () => {
    fetch('https://api.randomuser.me/ ')
    .then(response => response.json())
    .then(data => { 
      this.setState({
        persons: data.results,
        loading: false,
      });
    });
  }

  render (){
    const { persons, loading } = this.state;
    if (loading) return <loading />;
    return(
      <div>
        {persons.map(({ name, email, registered, picture }) => {
          return (
            <div key={name}> 
              <h3>{`${name.title} ${name.first} ${name.last}`}</h3>
              <img src={picture.large} alt={`${name.first} ${name.last}`} />
              <p>{email}</p>
              <p>{registered.age}</p>
            </div>
          )
        })}    
      </div>
    );
  }
}

export default PersonDetails;
